using System;
using System.Threading.Tasks;
using Volo.Abp.Data;
using Volo.Abp.DependencyInjection;
using Volo.Abp.Uow;
using Exam.Challenges;

namespace Exam.Challenges
{
    public class ChallengesDataSeedContributor : IDataSeedContributor, ISingletonDependency
    {
        private bool IsSeeded = false;
        private readonly IChallengeRepository _challengeRepository;
        private readonly IUnitOfWorkManager _unitOfWorkManager;

        public ChallengesDataSeedContributor(IChallengeRepository challengeRepository, IUnitOfWorkManager unitOfWorkManager)
        {
            _challengeRepository = challengeRepository;
            _unitOfWorkManager = unitOfWorkManager;

        }

        public async Task SeedAsync(DataSeedContext context)
        {
            if (IsSeeded)
            {
                return;
            }

            await _challengeRepository.InsertAsync(new Challenge
            (
                id: Guid.Parse("4882ddb4-f720-4b12-96dd-2d805da5c80f"),
                name: "",
                startDate: new DateTime(2004, 2, 20),
                endDate: new DateTime(2013, 4, 2),
                goal: 655913541,
                isActive: true
            ));

            await _challengeRepository.InsertAsync(new Challenge
            (
                id: Guid.Parse("b945f44e-348e-4521-a290-59dccf7a39e7"),
                name: "",
                startDate: new DateTime(2023, 4, 26),
                endDate: new DateTime(2015, 7, 18),
                goal: 827113300,
                isActive: true
            ));

            await _unitOfWorkManager!.Current!.SaveChangesAsync();

            IsSeeded = true;
        }
    }
}